mapper.select:
select a.employee_id, a.employee_name,a.post,a.job_number,a.dept_id,b.name as deptName from wk_hrm_employee as a left join wk_hrm_dept b on a.dept_id = b.dept_id left join wk_hrm_employee_quit_info c on a.employee_id = c.employee_id where a.is_del = 0 and (a.entry_status in (1,3) or (a.entry_status = 4 and c.plan_quit_time >= date_sub(#{endTime},interval 1 month ))) and entry_time <= #{endTime}
and a.employee_id in
#{employeeId}
and 1=2
group by a.employee_id

select a.s_emp_record_id,a.actual_work_day,a.need_work_day, b.employee_name,b.employee_id,b.post,c.name as deptName,b.job_number,b.is_del from wk_hrm_salary_month_emp_record a left join wk_hrm_employee b on a.employee_id = b.employee_id left join wk_hrm_dept c on b.dept_id = c.dept_id where a.s_record_id = #{data.sRecordId}
and b.employee_name like concat('%',#{data.employeeName},'%')
and b.job_number like concat('%',#{data.jobNumber},'%')
and b.dept_id = #{data.deptId}
and a.employee_id in
#{employeeId}
and 1=2

select b.employee_id from wk_hrm_salary_month_emp_record a
join wk_hrm_employee b on a.employee_id = b.employee_id and b.is_del = 0
where a.s_record_id = #{sRecordId}


		and a.employee_id in

			#{employeeId}



		and 1=2
